import React, { useState } from 'react';
import styled from 'styled-components';
import Nav from 'react-bootstrap/Nav';
import Link from 'next/link';
import DropDown from '../DropDown';
import { useRouter } from 'next/router';

const Styled = styled.section`
  border-bottom: 1px solid rgba(0, 0, 0, 0.2);

  .nav--tab {
    justify-content: space-between;
    align-items: center;
    max-width: 500px;
    margin: auto;
    color: var(--second-clr);
  }

  .dropdown--button {
    color: ${({ invert }) =>
      invert ? 'var(--second-clr)' : 'var(--white-clr)'};
    font-weight: bold;
    border-bottom: 2px solid transparent;
    transition: var(--ease--in--out--02s);
    cursor: pointer;

    &:focus + ul,
    &hover + ul {
      visibility: visible;
      opacity: 1;
      transform: translateY(0px);
    }

    &:hover,
    &:focus {
      outline: transparent;
      border-bottom-color: var(--grey-clr);
    }
  }

  .activeTab {
    border-bottom-color: var(--primary-clr);
  }
`;

const UserProfileTabs = () => {
  const [active, setActive] = useState('overview');

  const {
    query: { userProfile, userPage },
  } = useRouter();

  const dropItems = [
    {
      name: 'favorites',
      href: '/[userProfile]/[userPage]',
      as: `/${userProfile}/favorites`,
      dropItems: [
        {
          name: 'movies',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/favorites`,
        },
        {
          name: 'tv',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/favorites?media=tv`,
        },
      ],
    },
    {
      name: 'watchlist',
      href: '/[userProfile]/[userPage]',
      as: `/${userProfile}/watchlist`,
      dropItems: [
        {
          name: 'movies',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/watchlist`,
        },
        {
          name: 'tv',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/watchlist?media=tv`,
        },
      ],
    },
    {
      name: 'ratings',
      href: '/[userProfile]/[userPage]',
      as: `/${userProfile}/ratings`,
      dropItems: [
        {
          name: 'movies',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/ratings`,
        },
        {
          name: 'tv',
          href: '/[userProfile]/[userPage]',
          as: `/${userProfile}/ratings?media=tv`,
        },
      ],
    },
  ];

  console.log(active);
  return (
    <Styled className='userProfile--tabs'>
      <div className='inner'>
        <Nav className='nav--tab'>
          {/* OverView */}
          {/* <Link
            href='/[userProfile]/[userPage]'
            as={`/${userProfile}/overview`}
          > */}
          <button
            onClick={() => setActive('overview')}
            className={`dropdown--button ${
              active === 'overview' ? 'activeTab' : ''
            }`}
          >
            Overview
          </button>
          {/* </Link> */}
          <DropDown items={dropItems} forType invert active setActive />
        </Nav>
      </div>
    </Styled>
  );
};

export default UserProfileTabs;
