import React from 'react';
import styled from 'styled-components';
import SocialLinks from '../../SocialLinks';
import Facts from './Facts';
import Link from 'next/link';

const Styled = styled.section`
  display: grid;
  grid-row-gap: 1em;
  background-color: var(--accent-clr);
  padding: 2em 1em;
  * {
    color: var(--second-clr);
  }

  .title {
    font-size: 1.2rem;
    margin-bottom: 1em;
  }
`;

const ProfileStats = ({ data }) => {
  return (
    <Styled className='profile--stats'>
      <SocialLinks data={data.external_ids} homepage={data.homepage} />
      <Facts data={data} />
      <div className='profile--stats--genres'>
        <h3 className='title'>genres</h3>
        {data.genres.map(item => (
          <Link
            href={`/genre/[id]/[name]/${data.typeFor}`}
            as={`/genre/${item.id}/${item.name}/${data.typeFor}`}
          >
            <a className='profile--stats--genres__genre' key={item.name}>
              {item.name}
            </a>
          </Link>
        ))}
      </div>
    </Styled>
  );
};

export default ProfileStats;
