import React from 'react';
import styled from 'styled-components';
import { FaFacebookF, FaTwitter, FaInstagram } from 'react-icons/fa';
import SocialLinks from '../SocialLinks';
import timeConvert from '../../utility/timeConvert';

const Styled = styled.section`
  display: grid;
  grid-row-gap: 1em;
  background-color: var(--accent-clr);
  padding: 2em 1em;
  * {
    color: var(--second-clr);
  }

  p {
    margin-bottom: 0.1em;
  }

  .stat--title {
    font-weight: bold;
    margin-bottom: 0.1em;
    text-transform: capitalize;
  }
`;

const ProfileStats = ({ data }) => {
  //   console.log(
  //     data.release_dates.results.filter(item => item.iso_3166_1 === 'US')
  //   );
  return (
    <Styled className='profile--stats'>
      <SocialLinks data={data.external_ids} homepage={data.homepage} />
      <div className='profile--stats--facts'>
        <h3 className='facts--title'>Facts</h3>
        <div className='stat'>
          <p className='stat--title'>Status</p>
          <span className='stat--subtext'>{data.status}</span>
        </div>
        <div className='stat'>
          <p className='stat--title'>Release Information</p>
          <ul className='release--content'>
            {data.release_dates.results
              .filter(item => item.iso_3166_1 === 'US')[0]
              .release_dates.map((item, i) => (
                <li key={i}>
                  <div className='release--date'>
                    <span className='location'>Flag</span>
                    <p className='stat--subtext'>
                      {item.release_date.slice(0, 10)}
                    </p>
                  </div>
                  <div className='certification'>
                    <span className='rate'>{item.certification}</span>
                    <p className='stat--subtext'>{item.type}</p>
                  </div>
                </li>
              ))}
          </ul>
        </div>
        <div className='stat'>
          <p className='stat--title'>Original Language</p>
          <span className='stat--subtext'>{data.original_language}</span>
        </div>
        <div className='stat'>
          <p className='stat--title'>Runtime</p>
          <span className='stat--subtext'>{timeConvert(data.runtime)}</span>
        </div>
        <div className='stat'>
          <p className='stat--title'>Budget</p>
          <span className='stat--subtext'>${data.revenue}</span>
        </div>
      </div>
    </Styled>
  );
};

export default ProfileStats;
