import { useContext, useEffect, useState } from 'react';
import MovieContext from '../context/MovieContext';
import { useRouter } from 'next/router';
import styled from 'styled-components';
import Form from 'react-bootstrap/Form';
import { FaSearch } from 'react-icons/fa';
import { CLEAR_SEARCH_DATA } from '../context/types';

const Styled = styled.div`
  background-color: var(--white-clr);
  padding: 0 1em;

  .form {
    max-width: 920px;
    margin: 0 auto;
  }

  .form-group {
    display: flex;
    margin: 0;
  }
  .form-label {
    margin-right: 0.5em;
    display: flex;
    align-items: center;
    justify-content: center;
    svg {
      color: var(--grey-clr);
    }
  }

  .form-control {
    border: 0;
  }
`;

const SearchFrom = () => {
  const { getSearchData, clearData } = useContext(MovieContext);
  const [search, setSearch] = useState('');

  const router = useRouter();

  const handleChange = e => setSearch(e.target.value);

  useEffect(() => {
    if (search === '') {
      console.log('emty', search);
      clearData(CLEAR_SEARCH_DATA);
      return;
    } else {
      getSearchData(search);
      console.log('sea', search);
    }
  }, [search]);

  const handleSubmit = async e => {
    e.preventDefault();

    if (search === '') {
      console.log('enter something');
    } else {
      console.log('loged', search);
      router.push('/search/[query]', `/search/${search}`);
    }
    setSearch('');
  };

  return (
    <Styled classname='search form'>
      <Form className='form' onSubmit={handleSubmit}>
        <Form.Group controlId='search'>
          <Form.Label>
            <FaSearch />
          </Form.Label>
          <Form.Control
            type='text'
            name='search'
            value={search}
            onChange={handleChange}
            placeholder='Search for a movie, tv show, person...'
          />
        </Form.Group>
      </Form>
    </Styled>
  );
};

export default SearchFrom;
