import React from 'react';
import Layout from '../components/layout';
import FormStyle from '../components/ui/Forms/formstyle';
import useForm from '../components/Hooks/useForm';
import { Button } from 'react-bootstrap';
import Link from 'next/link';
import styled from 'styled-components';

const Styled = styled.section`
  margin-top: 150px;
  color: var(--second-clr);

  .header {
    font-size: 1.4rem;
    font-weight: bold;
  }
`;

const signup = () => {
  const submit = () => {
    console.log(form);
  };

  const { handleChange, handleSubmit, form, setForm } = useForm(
    {
      username: '',
      email: '',
      password: '',
      password2: ''
    },
    submit
  );

  const { username, email, password, password2 } = form;
  return (
    <Layout>
      <Styled className='sign--up container'>
        <h2 className='header'>Sign up for an account</h2>
        <p className='para'>
          Signing up for an account is free and easy. Fill out the form below to
          get started.
        </p>
        <form className='form login--from' onSubmit={handleSubmit}>
          <div className='form-group'>
            <label htmlFor='username'>Username</label>
            <input
              id='username'
              name='username'
              value={username}
              onChange={handleChange}
              type='text'
              className='form-control'
              aria-describedby='username'
              placeholder='Enter username'
              // required
            />
          </div>
          <div className='form-group'>
            <label htmlFor='email'>Email address</label>
            <input
              id='email'
              name='email'
              value={email}
              onChange={handleChange}
              type='email'
              className='form-control'
              aria-describedby='emailHelp'
              placeholder='Enter email'
              // required
            />
          </div>
          <div className='form-group'>
            <label htmlFor='password'>Password</label>
            <input
              id='password'
              name='password'
              value={password}
              onChange={handleChange}
              type='password'
              className='form-control'
              placeholder='Enter password'
              minLength='6'
              // required
            />
          </div>
          <div className='form-group'>
            <label htmlFor='password2'>Confirm password</label>
            <input
              id='password2'
              name='password2'
              value={password2}
              onChange={handleChange}
              type='password'
              className='form-control'
              placeholder='Enter password again'
              minLength='6'
            />
          </div>
          <Button variant='primary' type='submit'>
            Sign Up
          </Button>
          <Link href='/'>
            <a className='cancel'>Cancel</a>
          </Link>
        </form>
      </Styled>
    </Layout>
  );
};

export default signup;
