import styled from 'styled-components';
import movieDB from '../../components/utility/movieDB';
import { IMG_URL_OR, IMG_URL } from '../../components/context/types';
import CirclePercentage from '../../components/ui/CirclePercentage';
import TagGroup from '../../components/ui/TagGroup';
import Link from 'next/link';

const Styled = styled.section`
  background: var(--bg-gradient);
  margin-top: 123px;

  .profile--backdrop {
    background: linear-gradient(rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.2)),
      url(${({ bgImg }) => `${IMG_URL_OR}${bgImg}`}) no-repeat center;
    min-height: 400px;
    background-size: cover;
    background-position: top;
    object-fit: cover;
    position: relative;

    &__img {
      position: absolute;
      bottom: -20vw;
      left: 20px;
      width: 120px;
    }
  }

  .profile--header {
    position: relative;
    padding: 2em 0.5em;
  }

  .profile--header__content {
    .title {
      padding-left: calc(32vw + 20px);
      font-size: calc(0.5em + 3vw);
      margin-bottom: 1em;
    }

    .rating,
    .meta {
      display: flex;
      align-items: center;
      padding: 0 0.5em;
    }

    .rating {
      margin: 1em 0;
      svg {
        margin-right: calc(3vw + 0.5em);
      }

      .tag--group {
        border-left: 1px solid #41494e;
        padding-left: 1.5em;
      }
    }
  }

  .meta {
    justify-content: space-between;
    font-size: 0.85rem;

    &--tab {
      border-right: 1px solid #41494e;
      padding-right: 1em;
      text-align: center;
    }
    &--tab:last-child {
      border: none;
    }
  }

  .crew {
    margin: 1em 0;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-gap: 1em;
  }
`;

const credits = arry => {
  const arrData = arry.reduce((acc, obj) => {
    let key = obj.name;
    if (!acc[key]) {
      acc[key] = [];
    }
    acc[key].push(obj);
    return acc;
  }, []);

  const joinData = Object.entries(arrData)
    .map(([key, value]) => {
      let department = value.map(item => item.department);
      let item = arrData[key].map(item => ({ ...item, department }));
      return item;
    })
    .sort((a, b) => b.length - a.length);

  return joinData;
};

const MovieProfile = ({ movie, typeFor }) => {
  console.log('profile', movie);

  return (
    <Styled className='profile' bgImg={movie.backdrop_path}>
      <div className='profile--backdrop'>
        <div className='profile--backdrop__img'>
          <img
            className='poster'
            src={`${IMG_URL}${movie.poster_path || movie.profile_path}`}
            alt={movie.title || movie.name}
          />
        </div>
      </div>
      <div className='profile--header'>
        {/* <div className='profile--header__img'>
          <img
            className='poster'
            src={`${IMG_URL}${movie.poster_path || movie.profile_path}`}
            alt={movie.title || movie.name}
          />
        </div> */}
        <div className='profile--header__content'>
          <h2 className='title'>{movie.title || movie.name}</h2>
          <div className='rating'>
            <CirclePercentage value={movie.vote_average * 10} />
            <TagGroup />
          </div>
          <div className='meta'>
            <span className='release meta--tab'>2017</span>
            <span className='runtime meta--tab'>1h 02 mins</span>
            <span className='rating meta--tab'>R</span>
            <span className='genre meta--tab'>Action,Drama,Sci-fi</span>
          </div>
          <div className='overview'>{movie.overview}</div>
          <div className='crew'>
            {credits(movie.credits.crew).map(
              (c, i) =>
                i < 6 && (
                  <div key={c[0].id} className='crew--person'>
                    <Link href='/person/[id]' as={`/person/${c[0].id}`}>
                      {c[0].name}
                    </Link>
                    <small className='d-block mt-1'>
                      {c[0].department.join(', ')}
                    </small>
                  </div>
                )
            )}
          </div>
        </div>
      </div>
    </Styled>
  );
};

MovieProfile.getInitialProps = async ctx => {
  console.log(ctx);
  try {
    const movie = await movieDB(
      ctx.asPath.slice(1),
      'append_to_response=account_states,external_ids,images,keywords,release_dates,videos,recommendations,reviews,credits'
    );
    return {
      movie
    };
  } catch (error) {
    console.log(error);
  }
};

export default MovieProfile;
