import React from 'react';
import Layout from '../../components/layout';
import styled from 'styled-components';
import movieDB from '../../components/utility/movieDB';
import CardTwo from '../../components/ui/Cards/CardTwo';
import { useContext } from 'react';
import MovieContext from '../../components/context/MovieContext';
import Pagination from '../../components/ui/Pagination';
import { useEffect } from 'react';
import { useState } from 'react';

const Styled = styled.section`
  background: var(--bg-dark-gradient);
  padding: 150px 1em 5em 1em;

  .wrapper {
    max-width: 1500px;
    margin: 0 auto;

    .title {
      font-size: 1.5rem;
      margin-bottom: 1em;
    }
  }

  .content--container {
    display: grid;
    grid-gap: 2em;
    margin-bottom: 2em;
  }

  @media screen and (min-width: 768px) {
    .content--container {
      grid-row-gap: 3.5em;
      grid-template-columns: repeat(2, 1fr);
    }
  }
`;

const movie = ({ movies }) => {
  const { paginate } = useContext(MovieContext);
  const [pageOfItems, setPageOfItems] = useState([]);

  useEffect(() => {
    paginate(movies.page);
  }, [movies.page]);

  const pageNumbers = total_pages => {
    const pageNumber = [];
    for (let i = 1; i <= total_pages; i++) {
      pageNumber.push(i);
    }
    return pageNumber;
  };

  const onChangePage = pageOfItems => setPageOfItems(pageOfItems);

  console.log(movies, movies.title.split(' ').join('_'));
  return (
    <Layout>
      <Styled className='movie--section'>
        <div className='wrapper'>
          <h2 className='title'>{movies.title} Movies</h2>
          <div className='content--container'>
            {movies.results.map(item => (
              <CardTwo key={item.id} data={item} typeFor={movies.typeFor} />
            ))}
          </div>
          {/* <Pagination
            total_pages={movies.total_pages}
            typeFor={`/movie?query=${movies.title.split(' ').join('_')}&page=`}
          /> */}
          <Pagination item={pageNumbers(500)} onChangePage={onChangePage} />
        </div>
      </Styled>
    </Layout>
  );
};

movie.getInitialProps = async ({ query }) => {
  const queryLeng = Object.keys(query).length;
  try {
    const movies = await movieDB(
      `movie/${queryLeng === 0 ? 'popular' : query.query}`,
      `page=${query.page ? query.page : 1}`
    );
    return {
      movies: {
        typeFor: 'movie',
        title: queryLeng === 0 ? 'popular' : query.query.split('_').join(' '),
        ...movies
      }
    };
  } catch (error) {
    console.log(error);
  }
};

export default movie;
