import React from 'react';
import Layout from '../components/layout';
import FormStyle from '../components/ui/Forms/formstyle';
import useForm from '../components/Hooks/useForm';

const signup = () => {
  const { handleChange, handleSubmit, form, setForm } = useForm(
    {
      name: '',
      email: '',
      password: '',
      password2: ''
    },
    submit
  );

  const { name, email, password, password2 } = form;
  return (
    <Layout>
      <div className='container'>
        <FormStyle>
          {/* <Banner
          title='Sign Up'
          subtitle='Take the step and join the team'
          lineAfter
          titleClr='var(--primary--clr)'
        /> */}
          <form className='form login--from' onSubmit={handleSubmit}>
            <div className='form-group'>
              <label htmlFor='name'>Name</label>
              <input
                id='name'
                name='name'
                value={name}
                onChange={handleChange}
                type='text'
                className='form-control'
                aria-describedby='name'
                placeholder='Enter name'
                // required
              />
            </div>
            <div className='form-group'>
              <label htmlFor='email'>Email address</label>
              <input
                id='email'
                name='email'
                value={email}
                onChange={handleChange}
                type='email'
                className='form-control'
                aria-describedby='emailHelp'
                placeholder='Enter email'
                // required
              />
            </div>
            <div className='form-group'>
              <label htmlFor='password'>Password</label>
              <input
                id='password'
                name='password'
                value={password}
                onChange={handleChange}
                type='password'
                className='form-control'
                placeholder='Enter password'
                minLength='6'
                // required
              />
            </div>
            <div className='form-group'>
              <label htmlFor='password2'>Confirm password</label>
              <input
                id='password2'
                name='password2'
                value={password2}
                onChange={handleChange}
                type='password'
                className='form-control'
                placeholder='Enter password again'
                minLength='6'
              />
            </div>
            {/* <Button title='Submit' type='submit' addClass='btn-block' /> */}
          </form>
        </FormStyle>
      </div>
    </Layout>
  );
};

export default signup;
