export default (arr, sortBy) => {
  return arr
    .map(i => {
      const year = i.release_date ? 'release_date' : 'first_air_date';
      i[year] =
        i[year] === undefined || i[year] === null ? '' : i[year].slice(0, 4);
      return { ...i, date: i[year] };
    })
    .sort((a, b) => b.date - a.date);
};

