import styled from 'styled-components';
import Socials from './ui/socials';
import Link from 'next/link';

const Styled = styled.footer`
  margin-top: auto;
  display: grid;
  position: relative;
  text-align: center;
  background: url('/ticketBg.jpg');
  background-size: cover;
  background-repeat: no-repeat;
  object-fit: cover;
  background-position: center;
  z-index: 1;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: rgba(0, 0, 0, 0.85);
    z-index: -1;
  }

  p {
    color: var(--white-clr);
  }

  .content {
    display: grid;
    grid-gap: 1em;
    gap: 1em;
    padding: 2em 0;
    max-width: 1400px;
    margin: 0 auto;
  }

  .logo {
    img {
      width: 80px;
      margin: 0 auto;
    }
  }

  .copyRight {
    padding: 1em 0;
    align-self: end;
  }

  @media screen and (min-width: 1024px) {
    .content {
      grid-template-columns: repeat(3, 1fr);
      padding: 3em 0;
    }
  }
`;

const Footer = () => (
  <Styled>
    <div className='content'>
      <Link href='/'>
        <a className='logo'>
          <img src='/popcorn_logo.png' alt='popcorn logo' />
          PopCorn Fun
        </a>
      </Link>
    </div>
    <div className='copyRight'>
      <p>Â© PopCorn Fun 2020</p>
    </div>
  </Styled>
);

export default Footer;
