import { useContext } from 'react';
import MovieContext from './context/MovieContext';
import styled from 'styled-components';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Link from './Link';
import NextLink from 'next/link';
import SearchFrom from './ui/SearchFrom';

const Styled = styled.header`
  position: fixed;
  top: 0;
  right: 0;
  left: 0;
  z-index: 1030;
  background-image: var(--bg-gradient);
  .navbar {
    max-width: 1500px;
    margin: 0 auto;
  }

  .navbar-toggler {
    background-color: var(--white-clr);
  }

  .navbar-nav .nav-link,
  .navbar-brand {
    color: var(--white-clr);
    font-weight: bold;

    &:hover,
    &:focus {
      color: var(--primary-clr);
    }
  }
  /* Change Nav Links Active */
  .selected {
    color: var(--primary-clr) !important;
  }

  .navbar-brand {
    width: 30px;

    img {
      display: inline-block;
      margin-right: 0.5em;
    }
  }

  @media screen and (min-width: 1000px) {
    .navbar-collapse {
      margin-left: 13em;
    }

    .navbar-brand {
      width: 40px;
    }
  }
`;

const NavCustom = () => {
  const { navScroll, navRef } = useContext(MovieContext);

  return (
    <Styled className='header' ref={navRef} navScroll={navScroll}>
      <Navbar collapseOnSelect expand='lg'>
        <NextLink href='/'>
          <a className='navbar-brand'>
            <img src='/popcorn_logo.png' alt='popcorn logo' />
            PopCorn Fun
          </a>
        </NextLink>
        <Navbar.Toggle aria-controls='responsive-navbar-nav' />
        <Navbar.Collapse id='responsive-navbar-nav'>
          <Nav className='mr-auto'>
            <Link href='/discover'>
              <a className='nav-link' role='button'>
                Discover
              </a>
            </Link>
            <Link href='/movie'>
              <a className='nav-link' role='button'>
                Movies
              </a>
            </Link>
            <Link href='/tv'>
              <a className='nav-link' role='button'>
                Tv Shows
              </a>
            </Link>
            <Link href='/person'>
              <a className='nav-link' role='button'>
                People
              </a>
            </Link>
          </Nav>
          <Nav>
            <Link href='/login'>
              <a className='nav-link' role='button'>
                Log In
              </a>
            </Link>
            <Link href='/signup'>
              <a className='nav-link' role='button'>
                Sign Up
              </a>
            </Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
      <SearchFrom />
    </Styled>
  );
};

export default NavCustom;
