import React from 'react';
import Link from '../Link';
// import {Link as NextLink} from 'next/link';
import styled from 'styled-components';
import { IoIosArrowDown } from 'react-icons/io';

const Styled = styled.div`
  position: relative;

  ul {
    margin-top: 10px;
    position: absolute;
    display: flex;
    justify-content: space-around;
    flex-direction: column;
    align-items: center;
    width: 200px;
    height: 200px;
    background: white;
    left: 0px;
    list-style: none;
    border-radius: 5px;
    opacity: 0;
    pointer-events: none;
    transform: translateY(-10px);
    transition: all 0.4s ease;
  }

  li {
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;

    &:hover {
      background-color: rgb(197, 173, 181);
    }

    .dropdown-item {
      color: black;
      text-decoration: none;
    }
  }

  .dropdown--link,
  .dropdown--button {
    color: var(--white-clr);
    font-weight: bold;
    border-bottom: 2px solid transparent;
    transition: var(--ease--in--out--02s);
    cursor: pointer;

    &:hover {
      color: var(--white-clr);
      border-bottom-color: var(--primary-clr);
    }
  }

  button:focus > ul {
    opacity: 1;
    pointer-events: all;
    transform: translateY(0px);
  }
`;

const DropDown = ({ items, forType }) => {
  return items.map((item) => (
    <Styled className='dropDown' key={item.name}>
      {!forType && (
        <Link href={item.href} as={item.as}>
          <a className='dropdown--link'>
            {item.name} <IoIosArrowDown className='nav--arrow' />
          </a>
        </Link>
      )}
      <button className='dropdown--button'>
        {item.name} <IoIosArrowDown className='nav--arrow' />
      </button>
      <ul>
        {item.dropItems.map(({ href, as, name }) => (
          <li key={name}>
            <Link href={href} as={as}>
              <a className='dropdown-item' role='button'>
                {name}
              </a>
            </Link>
          </li>
        ))}
      </ul>
    </Styled>
  ));
};

export default DropDown;
