/* Pagination Component 
-------------------------------------------------*/
import React from 'react';
import { useEffect } from 'react';

// const propTypes = {
//   items: React.PropTypes.array.isRequired,
//   onChangePage: React.PropTypes.func.isRequired,
//   initialPage: React.PropTypes.number
// };

const defaultProps = {
  initialPage: 1
};

const Pagination = ({ items, initialPage, onChangePage }) => {
  const [pager, setPager] = useState({});

  useEffect(() => {
    // set page if items array isn't empty
    if (items && items.length) {
      setPage(initialPage);
    }

    // reset page if items array has changed
    setPager(prevProps => {
      if (items !== prevProps.items) {
        setPage(initialPage);
      }
    });
  }, [items]);

  const setPage = page => {
    if (page < 1 || page > pager.totalPages) {
      return;
    }

    // get new pager object for specified page
    pager = getPager(items.length, page);

    // get new page of items from items array
    var pageOfItems = items.slice(pager.startIndex, pager.endIndex + 1);

    // update state
    setPager(pager);

    // call change page function in parent component
    onChangePage(pageOfItems);
  };

  const getPager = (totalItems, currentPage, pageSize) => {
    // default to first page
    currentPage = currentPage || 1;

    // default page size is 10
    pageSize = pageSize || 10;

    // calculate total pages
    var totalPages = Math.ceil(totalItems / pageSize);

    var startPage, endPage;

    if (totalPages <= 10) {
      // less than 10 total pages so show all
      startPage = 1;
      endPage = totalPages;
    } else {
      // more than 10 total pages so calculate start and end pages
      if (currentPage <= 6) {
        startPage = 1;
        endPage = 10;
      } else if (currentPage + 4 >= totalPages) {
        startPage = totalPages - 9;
        endPage = totalPages;
      } else {
        startPage = currentPage - 5;
        endPage = currentPage + 4;
      }
    }

    // calculate start and end item indexes
    var startIndex = (currentPage - 1) * pageSize;
    var endIndex = Math.min(startIndex + pageSize - 1, totalItems - 1);

    // create an array of pages to ng-repeat in the pager control
    var pages = [...Array(endPage + 1 - startPage).keys()].map(
      i => startPage + i
    );

    // return object with all pager properties required by the view
    return {
      totalItems: totalItems,
      currentPage: currentPage,
      pageSize: pageSize,
      totalPages: totalPages,
      startPage: startPage,
      endPage: endPage,
      startIndex: startIndex,
      endIndex: endIndex,
      pages: pages
    };
  };

  if (!pager.pages || pager.pages.length <= 1) {
    // don't display pager if there is only 1 page
    return null;
  }

  return (
    <ul className='pagination'>
      <li className={pager.currentPage === 1 ? 'disabled' : ''}>
        <a onClick={() => setPage(1)}>First</a>
      </li>
      <li className={pager.currentPage === 1 ? 'disabled' : ''}>
        <a onClick={() => setPage(pager.currentPage - 1)}>Previous</a>
      </li>
      {pager.pages.map((page, index) => (
        <li key={index} className={pager.currentPage === page ? 'active' : ''}>
          <a onClick={() => setPage(page)}>{page}</a>
        </li>
      ))}
      <li className={pager.currentPage === pager.totalPages ? 'disabled' : ''}>
        <a onClick={() => setPage(pager.currentPage + 1)}>Next</a>
      </li>
      <li className={pager.currentPage === pager.totalPages ? 'disabled' : ''}>
        <a onClick={() => setPage(pager.totalPages)}>Last</a>
      </li>
    </ul>
  );
};

