import Layout from '../components/layout';
import axios from 'axios';
import { BASE_URL, API_KEY } from '../components/context/types';
import VideoHero from '../components/ui/VideoHero';
import Contact from '../components/contact';
import PopularSection from '../components/ui/PopularSection';
import movieDB from '../components/utility/movieDB';

const IndexPage = ({ movieNowPlay, tvOnAir, action }) => {
  return (
    <Layout>
      <VideoHero />
      <PopularSection
        data={movieNowPlay}
        typeFor='movie'
        title={
          <>
            in theaters <br /> now to enjoy
          </>
        }
        subtitle='Most watched movies'
        toLink='/movie'
      />
      <PopularSection
        data={tvOnAir}
        typeFor='tv'
        title={
          <>
            Popular TV <br />
            Series Right Now
          </>
        }
        subtitle='Most watched tv'
        toLink='/tv'
      />
      <Contact />
    </Layout>
  );
};

IndexPage.getInitialProps = async () => {
  try {
    const tv = await movieDB('tv/on_the_air');
    const movie = await movieDB('movie/now_playing');
    const action = await movieDB(
      'discover/movie',
      'with_genres=28&sort_by=vote_count.desc'
    );

    return {
      action: action.results.slice(0, 6),
      movieNowPlay: movie.results,
      tvOnAir: tv.results
    };
  } catch (error) {
    console.log(error);
    return {
      movieNowPlay: null,
      tvOnAir: null
    };
  }
};

export default IndexPage;
