import axios from 'axios';
import { API_KEY, BASE_URL } from '../context/types';

const movieDB = async (url, query = null, method = 'get') => {
  try {
    const res = await axios[method](
      `${BASE_URL}${url}?${API_KEY}&language=en-US${query && '&' + query}`
    );
    console.log(res);
    return res;
  } catch (error) {
    console.log(error);
    return {
      error: 'sorry something went wrong'
    };
  }
};

export default movieDB;
